mapper.select:
AND AUTH.AUTH_CD LIKE CONCAT('%', #{search}, '%') OR AUTH.AUTH_NM LIKE CONCAT('%', #{search}, '%')
ORDER BY AUTH.AUTH_SEQ
LIMIT #{pagination.limitPerPage} OFFSET #{pagination.offset}

mapper.update:
UPDATE TB_AUTH 
SET
    AUTH_NM = #{authNm}
	, USE_YN = #{useYn}
	, UPT_DT = CURRENT_TIMESTAMP
	, UPT_SEQ = #{loginUserSeq}
WHERE AUTH_SEQ = #{authSeq}

UPDATE TB_AUTH_MENU
SET
    READ_YN = #{readYn}
    , WRITE_YN = #{writeYn}
	, EXEC_YN = #{execYn}
	, USE_YN = #{useYn}
	, UPT_DT = CURRENT_TIMESTAMP
	, UPT_SEQ = #{loginUserSeq}
WHERE AUTH_MENU_SEQ = #{authMenuSeq}
