AND PROJ.PROJ_NM LIKE CONCAT('%', #{search}, '%') OR PROJ.PROJ_SW_NM LIKE CONCAT('%', #{search}, '%') OR COMP.COMP_NM LIKE CONCAT('%', #{search}, '%')
ORDER BY PROJ.PROJ_SEQ DESC
