SELECT COUNT(PROJ.PROJ_SEQ) FROM TB_PROJ PROJ INNER JOIN TB_COMPANY COMP ON PROJ.COMP_SEQ = COMP.COMP_SEQ WHERE PROJ.USE_YN = 'Y'
AND PROJ.COMP_SEQ = #{compSeq}
AND PROJ.PROJ_NM LIKE CONCAT('%', #{search}, '%') OR PROJ.PROJ_SW_NM LIKE CONCAT('%', #{search}, '%') OR COMP.COMP_NM LIKE CONCAT('%', #{search}, '%')

mapper.insert:
INSERT INTO TB_PROJ
(
    COMP_SEQ
    , PROJ_NM
    , PROJ_SW_NM
    , PROJ_STD_YMD
    , PROJ_END_YMD
	, USE_YN
	, REG_DT
	, REG_SEQ
) VALUES (
    #{compSeq}
	, #{projNm}
	, #{projSwNm}
	, #{projStdYmd}
	, #{projEndYmd}
	, #{useYn}
	, CURRENT_TIMESTAMP
	, #{regSeq}
)

mapper.update:
UPDATE TB_PROJ 
SET
    PROJ_NM = #{projNm}
	, PROJ_SW_NM = #{projSwNm}
	, PROJ_STD_YMD = #{projStdYmd}
	, PROJ_END_YMD = #{projEndYmd}
	, USE_YN = #{useYn}
	, UPDT_DT = CURRENT_TIMESTAMP
	, UPDT_SEQ = #{updtSeq}
WHERE PROJ_SEQ = #{projSeq}
