mapper.insert:
INSERT INTO TB_REQ_CMT
(
    REQ_SEQ
    , REQ_HIS_SEQ
	, REQ_CMT
	, USE_YN
	, REG_SEQ
) VALUES (
	#{reqSeq}
	, #{reqHisSeq}
	, #{reqCmt}
	, #{useYn}
	, #{loginUserSeq}
)

mapper.update:
UPDATE TB_REQ_CMT 
SET
	REQ_CMT = #{reqCmt}
	, useYn = #{useYn}
	, UPT_DT = CURRENT_TIMESTAMP
	, UPT_SEQ = #{loginUserSeq}
WHERE REQ_CMT_SEQ = #{reqCmtSeq}
