mapper.insert:
		INSERT INTO TB_REQ_DTL
		(
		    REQ_DTL_NO
		    , REQ_SEQ
			, REQ_NO
			, REQ_DTL_NM
			, REQ_DTL_CONTENT_TXT
			, DTL_SITE_GB_CD
			, REQ_GB_CD
			, SITE_GB_CD_STR
			, REQ_DTL_STAT_CD
			, TGT_YMD
			, TGT_MM
			, DEL_YN
			, REG_DT
			, REG_SEQ
		) VALUES (
		    (
				SELECT
					CONCAT(#{reqNo}, '-', LPAD(COALESCE(MAX(SUBSTR(S_REQ_DTL.REQ_DTL_NO, 12)), 0) + 1, 2, 0))
				FROM TB_REQ_DTL AS S_REQ_DTL
                WHERE S_REQ_DTL.REQ_NO = #{reqNo}
				LIMIT 1	    
		    )
			, #{reqSeq}
			, #{reqNo}
			, #{reqDtlNm}
			, #{reqDtlContentTxt}
			, #{dtlSiteGbCd}
			, #{reqGbCd}
			, #{siteGbCdStr}
			, '00'
			, #{tgtYmd}
			, #{tgtMm}
			, 'N'
			, CURRENT_TIMESTAMP
			, #{regSeq}
		)		

        	SELECT 
        		REQ_DTL_SEQ
        		, REQ_DTL_NO
        		, REQ_DTL_STAT_CD
        	FROM TB_REQ_DTL
        	WHERE REQ_DTL_SEQ = LAST_INSERT_ID()

mapper.update:
UPDATE TB_REQ_DTL 
SET
	REQ_DTL_NM = #{reqDtlNm}
	, REQ_DTL_CONTENT_TXT = #{reqDtlContentTxt}
	, TGT_YMD = #{tgtYmd}
	, TGT_MM = #{tgtMm}
	, UPDT_DT = CURRENT_TIMESTAMP
	, UPDT_SEQ = #{updtSeq}
WHERE REQ_DTL_SEQ = #{reqDtlSeq}

mapper.insert:
INSERT INTO TB_REQ_DTL_HIS
(
    REQ_DTL_SEQ
    , REQ_DTL_NO
	, REQ_DTL_STAT_CD
	, REG_YMD
	, REG_DT
	, REG_SEQ
) VALUES (
	#{reqDtlSeq}
	, #{reqDtlNo}
	, #{reqDtlStatCd}
	, DATE_FORMAT(CURRENT_TIMESTAMP, "%Y%m%d")
	, CURRENT_TIMESTAMP
	, #{regSeq}
)
