mapper.insert:
INSERT INTO TB_REQ_DTL_CMT
(
    REQ_DTL_SEQ
    , REQ_DTL_HIS_SEQ
	, REQ_DTL_CMT
	, USE_YN
	, REG_SEQ
) VALUES (
	#{reqDtlSeq}
	, #{reqDtlHisSeq}
	, #{reqDtlCmt}
	, #{useYn}
	, #{loginUserSeq}
)

mapper.update:
UPDATE TB_REQ_DTL_CMT 
SET
	REQ_DTL_CMT = #{reqDtlCmt}
	, useYn = #{useYn}
	, UPT_DT = CURRENT_TIMESTAMP
	, UPT_SEQ = #{loginUserSeq}
WHERE REQ_DTL_CMT_SEQ = #{reqDtlCmtSeq}
