mapper.insert:
INSERT INTO TB_REQ_DTL_CMT
(
    REQ_DTL_SEQ
    , REQ_DTL_NO
	, REQ_DTL_CMT
	, USER_SEQ
	, DEL_YN
	, REG_DT
	, REG_SEQ
) VALUES (
	#{reqDtlSeq}
	, #{reqDtlNo}
	, #{reqDtlCmt}
	, #{userSeq}
	, 'N'
	, CURRENT_TIMESTAMP
	, #{regSeq}
)

mapper.update:
UPDATE TB_REQ_DTL_CMT 
SET
   REQ_DTL_CMT = #{reqDtlCmt}
	, UPDT_DT = CURRENT_TIMESTAMP
	, UPDT_SEQ = #{updtSeq}
WHERE REQ_DTL_CMT_SEQ = #{reqDtlCmtSeq}
