mapper.insert:
		INSERT INTO TB_REQ_DTL_USER
		(
		    REQ_DTL_SEQ
		    , USER_SEQ
			, BUSI_ROLL_CD
			, PROC_YMD
			, PROC_HH
			, REQ_DTL_STAT_CD
			, REG_DT
			, REG_SEQ
		) VALUES (
			#{reqDtlSeq}
			, #{userSeq}
			, (SELECT USER_BUSI_CD FROM TB_USER WHERE USER_SEQ = #{userSeq})
			, #{procYmd}
			, #{procHh}
			, #{reqDtlStatCd}
			, CURRENT_TIMESTAMP
			, #{regSeq}
		)	

        	SELECT 
        		REQ_DTL_USER_SEQ
        		, BUSI_ROLL_CD
        	FROM TB_REQ_DTL_USER
        	WHERE REQ_DTL_USER_SEQ = LAST_INSERT_ID()

mapper.update:
UPDATE TB_REQ_DTL_USER 
SET
   PROC_YMD = #{procYmd}
	, PROC_HH = #{procHh}
	, REQ_DTL_STAT_CD = #{reqDtlStatCd}
	, REG_DT = CURRENT_TIMESTAMP
	, REG_SEQ = #{regSeq}			
WHERE REQ_DTL_USER_SEQ = #{reqDtlUserSeq}

mapper.insert:
INSERT INTO TB_REQ_DTL_USER_CMT
(
    REQ_DTL_USER_SEQ
    , USER_SEQ
	, REQ_DTL_STD_YMD
	, REQ_DTL_STD_PART
	, REQ_DTL_END_YMD
	, REQ_DTL_END_PART
	, REQ_DTL_STAT_CD
	, CMT
	, MNG_MAIN_YN
	, REG_DT
) VALUES (
	#{reqDtlUserSeq}
	, #{userSeq}
	, #{reqDtlStdYmd}
	, #{reqDtlStdPart}
	, #{reqDtlEndYmd}
	, #{reqDtlEndPart}
	, #{reqDtlStatCd}
	, #{cmt}
	, #{mngMainYn}
	, CURRENT_TIMESTAMP
)

mapper.update:
UPDATE TB_REQ_DTL_USER_CMT 
SET
   REQ_DTL_STD_YMD = #{reqDtlStdYmd}
	, REQ_DTL_STD_PART = #{reqDtlStdPart}
	, REQ_DTL_END_YMD = #{reqDtlEndYmd}
	, REQ_DTL_END_PART = #{reqDtlEndPart}
	, REQ_DTL_STAT_CD = #{reqDtlStatCd}
	, CMT = #{cmt}
	, MNG_MAIN_YN = #{mngMainYn}
	, REG_DT = CURRENT_TIMESTAMP
WHERE REQ_DTL_USER_SEQ = #{reqDtlUserSeq}
