ORDER BY SITE.SITE_SEQ DESC

	LIMIT #{pagination.limitPerPage} OFFSET #{pagination.offset}

mapper.insert:
INSERT INTO TB_SITE
(
    COMP_SEQ
    , PROJ_SEQ
    , SITE_NM
    , SITE_SW_NM
    , SITE_GB_CD
	, USE_YN
	, REG_DT
	, REG_SEQ
) VALUES (
    #{compSeq}
	, #{projSeq}
	, #{siteNm}
	, #{siteSwNm}
	, #{siteGbCd}
	, #{useYn}
	, CURRENT_TIMESTAMP
	, #{regSeq}
)

mapper.update:
UPDATE TB_SITE 
SET
    SITE_NM = #{siteNm}
	, SITE_SW_NM = #{siteSwNm}
	, SITE_GB_CD = #{siteGbCd}
	, USE_YN = #{useYn}
	, UPDT_DT = CURRENT_TIMESTAMP
	, UPDT_SEQ = #{updtSeq}
WHERE PROJ_SEQ = #{projSeq}
