mapper.select:
AND USER.USER_NM LIKE CONCAT('%', #{search}, '%') OR COMP.COMP_NM LIKE CONCAT('%', #{search}, '%') OR POSI.POSITION_NM LIKE CONCAT('%', #{search}, '%')
ORDER BY USER.USER_SEQ DESC
LIMIT #{pagination.limitPerPage} OFFSET #{pagination.offset}

mapper.insert:
INSERT INTO TB_USER
