	, DUTY_GB_CD = #{dutyGbCd}
	, COMP_SEQ = #{compSeq}
	, USER_TEL = #{userTel}
	, USER_HP = #{userHp}
	, USER_POST_NO = #{userPostNo}
	, USER_ADDR = #{userAddr}
	, USER_DTL_ADDR = #{userDtlAddr}
	, USER_CI_KEY = #{userCiKey}
	, USER_DI_KEY = #{userDiKey}
	, VERIFY_YN = #{verifyYn}
	, USE_YN = #{useYn}
	, USER_ROLE = #{userRole}
	, UPDT_DT = CURRENT_TIMESTAMP
